import React, {useState} from 'react';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8000";

function App(){
  const [q, setQ] = useState('');
  const [ans, setAns] = useState(null);
  const [loading, setLoading] = useState(false);

  const ask = async () => {
    setLoading(true);
    try{
      const r = await axios.post(API_URL + '/solve', {user_id:'demo', question:q});
      setAns(r.data);
    }catch(e){
      setAns({error: e.message});
    }
    setLoading(false);
  }

  return (<div style={{padding:20}}>
    <h2>Agentic-RAG Math Tutor (LangChain + Granite + Llama3.1)</h2>
    <textarea value={q} onChange={(e)=>setQ(e.target.value)} rows={6} cols={80} />
    <br/>
    <button onClick={ask} disabled={loading}>Ask</button>
    {loading && <p>Thinking...</p>}
    {ans && <div style={{marginTop:20}}>
      {ans.error ? (
        <div style={{color:'red'}}>
          <h3>Error:</h3>
          <p>{ans.error}</p>
        </div>
      ) : (
        <div>
          <h3>Source: {ans.source}</h3>
          <pre style={{whiteSpace:'pre-wrap'}}>{ans.answer}</pre>
        </div>
      )}
    </div>}
  </div>);
}

export default App;
